import React from "react";
import Footersss from "./components/header";
import logo from "./img/logo.jpg"
import userEvent from "@testing-library/user-event";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            helpTxt: "Help Text",
            userData: ""
        };

        this.inputClick = this.inputClick.bind(this);
    };

    render() {
        return (
    <div>
        <h1>{this.state.helpTxt}</h1>
        <h2>{this.state.userData}</h2>
        <input className="inputbtn" placeholder={this.state.helpTxt} onClick={this.inputClick} onMouseEnter={this.inputOver} onChange={event => this.setState({userData})}/>
        <p>{this.state.helpTxt === "Help Text" ? "Yes" : "No"}</p>
        <Footersss those="Bottom txt"/>
        <img alt="" src={logo}/>
    </div>);
    };

    inputClick () {
        this.setState({helpTxt: "Changed"});
        console.log("Clicked!");
    };
    inputOver () {
        console.log("Mouse over Input!");
    };

};

export default App;
