import React from "react";

class Users extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users
        }
    }
    render() {
        if(this.users.length > 0)
            return (
                <div className="usersList">
                    {this.users.map((user) => (<div className="cardOfUsers" key={user.id}>
                        <h3>{user.firstName} {user.lastName}</h3>
                        <p>{user.bio}</p>
                        <b>{user.isHappy == true ? "Happy :)" : "Not happy :("}</b>
                    </div>))}
                </div>) 
        else 
            return (
            <div className="cardOfUsers">
                <h3>List is empty</h3>
            </div>)
        };
};

export default Users;
