import React from "react";

class AddUser extends React.Component {
    render() {

        return (
            <form>
                <input required placeholder="First name:" onChange={(e) => this.setState({firstName: e.target.value})}/>
                <input required placeholder="Last name:" onChange={(e) => this.setState({lastName: e.target.value})}/>
                <textarea placeholder="Biografy" onChange={(e) => this.setState({bio: e.target.value})}></textarea>
                <input required placeholder="Age:" onChange={(e) => this.setState({age: e.target.value})}/>
                <div style={{display: "flex", alignItems: "center"}}>
                    <label htmlFor="isHappy">Are you happy?</label>
                    <input required type="checkbox" id="isHappy" onChange={(e) => this.setState({isHappy: e.target.value})}/>
                </div>
                <button type="button">Add</button>
            </form>
        )};
};

export default AddUser;
