import React from "react";
import Footersss from "./components/header";
import logo from "./img/logo.jpg"

class App extends React.Component {
    constructor() {
        super(props);
        this.state = {
            helpTxt: "Help Text",
        }
    };

    render() {
        return (
    <div>
        <h1>{this.state.helpTxt}</h1>
        <input className="inputbtn" placeholder={this.state.helpTxt} onClick={this.inputClick} onMouseEnter={this.inputOver}/>
        <p>{this.state.helpTxt === "Help Text" ? "Yes" : "No"}</p>
        <Footersss those="Bottom txt"/>
        <img src={logo}/>
    </div>);
    };

    inputClick () {
        this.state.helpTxt = "Changed";
        console.log("Clicked!");
    };
    inputOver () {
        console.log("Mouse over Input!")
    };

};

export default App;
