import React from "react";

class Footersss extends React.Component {
    render() {
        return (
    <footer>{"Hi "+this.props.those}</footer>
    )};
}

class App extends React.Component {
    helpTxt = "Help Text"
    render() {
        return (
    <div>
        <h1>{this.helpTxt}</h1>
        <input className="inputbtn" placeholder={this.helpTxt} onClick={this.inputClick} onMouseEnter={this.inputOver}/>
        <p>{this.helpTxt === "Help Text" ? "Yes" : "No"}</p>
        <Footersss those="Bottom txt"/>
        <Footersss those="Bottom txt!"/>
        <Footersss those="Bottom txt1"/>
    </div>)};

    inputClick () {console.log("Clicked!")};
    inputOver () {console.log("Mouse over Input!")};

};

export default App;
