import React from "react";
import User from "./user";

class Users extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [
                {
                    id:0,
                    firstName:"Anton",
                    lastName:"Limonovich",
                    age:40,
                    bio:"Lorem Ipsum",
                    isHappy:true
                },
                {
                    id:1,
                    firstName:"Ipsum",
                    lastName:"Antonovich",
                    age:32,
                    bio:"Lorem Ipsumovich",
                    isHappy:false
                }
            ]
        }
    }
    render() {
        if(this.state.users.length > 0)
            return (
                <div className="usersList">
                    {this.state.users.map((user) => (<div className="cardOfUsers" key={user.id}>
                        <h3>{user.firstName} {user.lastName}</h3>
                        <p>{user.bio}</p>
                        <b>{user.isHappy == true ? "Happy :)" : "Not happy :("}</b>
                    </div>))};
                </div>) 
        else 
            return (
            <div className="cardOfUsers">
                <h3>List is empty</h3>
            </div>)
        };
};

export default Users;
