import React from "react";
import * as ReactDOMClient from "react-dom/client";

const inputClick = () => console.log("Clicked!");
const inputOver = () => console.log("Mouse over Input!");

const helpTxt = "Help Text";

class Footersss extends React.Component {
    render() {
        return (
    <footer>
        Bottom of site
    </footer>)
    }
}

class App extends React.Component {
    render() {
        return (
    <div>
        <h1>{helpTxt}</h1>
        <input className="inputbtn" placeholder={helpTxt} onClick={inputClick} onMouseEnter={inputOver}/>
        <p>{helpTxt === "Help Text" ? "Yes" : "No"}</p>
        <Footersss />
    </div>)
    }

    inputClick = () => console.log("Clicked!");
    inputOver = () => console.log("Mouse over Input!");

};

const app = ReactDOMClient.createRoot(document.getElementById("root"));

app.render(<App />);
