import React from "react";

class AddUser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            firstName: "",
            lastName: "",
            bio: "",
            age: 1,
            isHappy: false 
        };
    };
    render() {
        return (
            <form ref={(element) => this.userForm = element}>
                <input  placeholder="First name:" onChange={(e) => this.setState({firstName: e.target.value})}/>
                <input  placeholder="Last name:" onChange={(e) => this.setState({lastName: e.target.value})}/>
                <textarea placeholder="Biografy" onChange={(e) => this.setState({bio: e.target.value})}></textarea>
                <input  placeholder="Age:" onChange={(e) => this.setState({age: e.target.value})}/>
                <div style={{display: "flex", alignItems: "center"}}>
                    <label htmlFor="isHappy">Are you happy?</label>
                    <input  type="checkbox" id="isHappy" onChange={(e) => this.setState({isHappy: e.target.checked})}/>
                </div>
                <button onClick={() => { 
                    this.userForm.reset();
                    this.props.onAdd({
                        firstName: this.state.firstName,
                        lastName: this.state.lastName,
                        bio: this.state.bio,
                        age: this.state.age,
                        isHappy: this.state.isHappy
                })}} type="button">Add</button>
            </form>
        )};
};

export default AddUser;
